function releaseCustomer(o) {
    $(".loading").removeClass("d-none"), fetch(release_url, {
        method: "POST",
        body: JSON.stringify({id: o, cpf_cnpj: "{{session('customerActive')->document}}"}),
        mode: "cors",
        headers: {
            "Content-Type": "application/json; charset=UTF-8",
            "x-csrf-token": $("meta[name='csrf-token']").attr("content")
        }
    }).then(o => o.json()).then(o => {
        "ERRO2" === o ? ($(".loading").addClass("d-none"), notifyMessage(409, "error", "Este cadastro foi liberado a menos de 30 dias!")) : "NOK" === o ? ($(".loading").addClass("d-none"), notifyMessage(409, "error", "Este cadastro n\xe3o est\xe1 bloqueado ou n\xe3o possu\xed boletos!")) : ($(".loading").addClass("d-none"), notifyMessage(200, "success", "Cadastro liberado com sucesso!"))
    }).catch(o => {
        console.log("Error: ", o.message)
    })
}

function notifyMessage(code, status, message){
    Swal.fire({
        icon: status,
        title: code != 200 ? 'Desculpe!' : code,
        html: message,
        timer: 7000
    });
}
